import{_ as e,o as n,c as a,e as l}from"./app.94cd83f3.js";const o={},d=l('<h1 id="netty\u7EC4\u4EF6\u548C\u8BBE\u8BA1" tabindex="-1"><a class="header-anchor" href="#netty\u7EC4\u4EF6\u548C\u8BBE\u8BA1" aria-hidden="true">#</a> Netty\u7EC4\u4EF6\u548C\u8BBE\u8BA1</h1><h2 id="_1-channel" tabindex="-1"><a class="header-anchor" href="#_1-channel" aria-hidden="true">#</a> 1. <code>Channel</code></h2><p>\u57FA\u672C\u7684I/O\u64CD\u4F5C(<code>bind()</code>,<code>connect()</code>,<code>read()</code>,<code>write()</code>)\u4F9D\u8D56\u4E8E\u5E95\u5C42\u7F51\u7EDC\u4F20\u8F93\u6240\u63D0\u4F9B\u7684\u539F\u8BED\u3002Netty\u7684<code>Channel</code>\u63A5\u53E3\u6240\u63D0\u4F9B\u7684API\uFF0C\u964D\u4F4E\u4E86\u76F4\u63A5\u4F7F\u7528<code>Socket</code>\u7C7B\u7684\u590D\u6742\u6027\uFF0C<code>Channel</code>\u662F\u9884\u5B9A\u4E49\u3001\u4E13\u95E8\u5316\u5B9E\u73B0\u7684\u5E7F\u6CDB\u7C7B\u5C42\u6B21\u7ED3\u6784\u7684\u6839</p><p><img src="https://s2.loli.net/2023/01/05/vFIXnbPAYBMkp4w.png" alt="Channel"></p><h2 id="_2-eventloop" tabindex="-1"><a class="header-anchor" href="#_2-eventloop" aria-hidden="true">#</a> 2. <code>EventLoop</code></h2><p><code>EventLoop</code>\u5B9A\u4E49\u4E86\u7528\u4E8E\u5904\u7406\u8FDE\u63A5\u7684\u751F\u547D\u5468\u671F\u4E2D\u6240\u53D1\u751F\u7684\u4E8B\u4EF6\u7684\u6838\u5FC3\u62BD\u8C61</p><p>The <code>EventLoop</code> defines Netty&#39;s core abstraction for handling events that occur during the lifetime of a connection.</p><p><img src="https://s2.loli.net/2023/01/05/QAZY7bn9Cj8EMFS.png" alt=""></p><ul><li>\u4E00\u4E2A<code>EventLoopGroup</code>\u5305\u542B\u4E00\u4E2A\u6216\u8005\u591A\u4E2A<code>EventLoop</code></li><li>\u4E00\u4E2A<code>EventLoop</code>\u5728\u5B83\u7684\u751F\u547D\u5468\u671F\u5185\u53EA\u548C\u4E00\u4E2AThread\u7ED1\u5B9A</li><li>\u6240\u6709\u6709<code>EventLoop</code>\u5904\u7406\u7684I/O\u4E8B\u4EF6\u90FD\u5C06\u5728\u5B83\u4E13\u6709\u7684Thread\u4E0A\u88AB\u5904\u7406</li><li>\u4E00\u4E2A<code>Channel</code>\u5728\u5B83\u7684\u751F\u547D\u5468\u671F\u5185\u53EA\u6CE8\u518C\u4E8E\u4E00\u4E2A<code>EventLoop</code></li><li>\u4E00\u4E2A<code>EventLoop</code>\u53EF\u80FD\u4F1A\u88AB\u5206\u914D\u7ED9\u4E00\u4E2A\u6216\u591A\u4E2A<code>Channel</code></li></ul><blockquote><p>\u4E00\u4E2A\u7ED9\u5B9AChannel\u7684I/O\u64CD\u4F5C\u90FD\u662F\u6709\u76F8\u540C\u7684Thread\u6267\u884C\uFF0C\u5B9E\u9645\u4E0A\u6D88\u9664\u4E86\u5BF9\u540C\u6B65\u7684\u9700\u8981</p></blockquote><h2 id="_3-channelfuture" tabindex="-1"><a class="header-anchor" href="#_3-channelfuture" aria-hidden="true">#</a> 3. <code>ChannelFuture</code></h2><p>Netty\u4E2D\u7D20\u6709\u7684I/O\u64CD\u4F5C\u90FD\u662F\u4E00\u90E8\u5206\uFF0C\u56E0\u4E3A\u4E00\u4E2A\u64CD\u4F5C\u53EF\u80FD\u4E0D\u4F1A\u7ACB\u523B\u8FD4\u56DE\uFF0C\u6240\u4EE5\u9700\u8981\u4E00\u79CD\u7528\u4E8E\u5728\u4E4B\u540E\u7684\u67D0\u4E2A\u65F6\u95F4\u70B9\u786E\u5B9A\u5176\u7ED3\u679C\u7684\u65B9\u6CD5\u3002Netty\u4E2D\u63D0\u4F9B\u4E86<code>ChannelFuture</code>\u4E2D\u53E3\uFF0C<code>addListener()</code>\u65B9\u6CD5\u6CE8\u518C\u4E86\u4E00\u4E2A<code>ChannelFutureListener</code>\uFF0C\u4EE5\u4FBF\u5728\u67D0\u4E2A\u64CD\u4F5C\u5B8C\u6210\u65F6(\u65E0\u8BBA\u662F\u5426\u6210\u529F)\u5F97\u5230\u901A\u77E5</p><h2 id="_4-channelhandler" tabindex="-1"><a class="header-anchor" href="#_4-channelhandler" aria-hidden="true">#</a> 4. <code>ChannelHandler</code></h2><p><code>ChannelHandler</code>\u5145\u5F53\u4E86\u6240\u6709\u5904\u7406\u5165\u7AD9\u548C\u51FA\u7AD9\u6570\u636E\u7684\u5E94\u7528\u7A0B\u5E8F\u903B\u8F91\u7684\u5BB9\u5668\uFF0C\u7531\u7F51\u7EDC\u4E8B\u4EF6\u89E6\u53D1\uFF0C\u53EF\u4E13\u95E8\u7528\u4E8E\u51E0\u4E4E\u4EFB\u4F55\u7C7B\u578B\u7684\u52A8\u4F5C\uFF0C\u4F8B\u5982\u5C06\u6570\u636E\u4ECE\u4E00\u79CD\u683C\u5F0F\u8F6C\u6362\u4E3A\u53E6\u4E00\u79CD\u683C\u5F0F\uFF0C\u6216\u8005\u5904\u7406\u8F6C\u6362\u8FC7\u7A0B\u4E2D\u6240\u629B\u51FA\u7684\u5F02\u5E38\u3002</p><p><code>ChannelInboundHandler</code>\u63A5\u6536\u5165\u7AD9\u4E8B\u4EF6\u548C\u6570\u636E\uFF0C\u7136\u540E\u88AB\u4E1A\u52A1\u903B\u8F91\u5904\u7406\u3002\u5F53\u7ED9\u8FDE\u63A5\u7684\u5BA2\u6237\u7AEF\u53D1\u9001\u54CD\u5E94\u65F6\uFF0C\u4E5F\u53EF\u4EE5\u4ECE<code>ChannelInboundHandler</code>\u51B2\u5237\u6570\u636E\u3002\u5E94\u7528\u7A0B\u5E8F\u7684\u4E1A\u52A1\u903B\u8F91\u901A\u5E38\u9A7B\u7559\u5728\u4E00\u4E2A\u6216\u8005\u591A\u4E2A\u7684<code>ChannelInboundHandler</code>\u4E2D</p><h2 id="_5-channelpipeline" tabindex="-1"><a class="header-anchor" href="#_5-channelpipeline" aria-hidden="true">#</a> 5. <code>ChannelPipeline</code></h2><p>ChannelPipeline\u63D0\u4F9B\u4E86ChannelHandler\u94FE\u7684\u5BB9\u5668(\u7B80\u5355\u7406\u89E3\u4E3A: ChannelPipeline\u4E2D\u7EF4\u62A4\u4E86\u4E00\u4E2A\u53CC\u5411\u94FE\u8868\u7528\u4E8E\u5B58\u653EChannelHandler),\u5E76\u5B9A\u4E49\u4E86\u7528\u4E8E\u5728\u8BE5\u94FE\u4E0A\u4F20\u64AD\u5165\u7AD9\u548C\u51FA\u7AD9\u4E8B\u4EF6\u6D41\u7684API</p><p>\u5F53Channel\u88AB\u521B\u5EFA\u65F6\uFF0C\u5B83\u4F1A\u88AB\u81EA\u52A8\u5730\u5206\u914D\u5230\u5B83\u4E13\u5C5E\u7684ChannelPipeline.</p><ul><li>\u4E00\u4E2AChannelInitializer\u7684\u5B9E\u73B0\u88AB\u6CE8\u518C\u5230ServerBootstrap\u4E2D</li><li>\u5F53ChannelInitializer.initChannel()\u65B9\u6CD5\u8C03\u7528\u65F6\uFF0CChannelInitializer\u5C06\u5728ChannelPipline\u4E2D\u5B89\u88C5\u4E00\u7EC4\u81EA\u5B9A\u4E49\u7684ChannelHandler</li><li>ChannelInitializer\u5C06\u5B83\u81EA\u5DF1\u4ECEChannelPipeline\u4E2D\u79FB\u9664</li></ul><p><img src="https://s2.loli.net/2023/01/08/lWY5hRx2gL1QDN9.png" alt=""></p><p>\u4ECE\u4E00\u4E2A\u5BA2\u6237\u7AEF\u5E94\u7528\u7A0B\u5E8F\u7684\u89D2\u5EA6\u6765\u770B\uFF0C\u5982\u679C\u4E8B\u4EF6\u7684\u8FD0\u52A8\u65B9\u5411\u662F\u4ECE\u5BA2\u6237\u7AEF\u5230\u670D\u52A1\u5668\u7AEF\uFF0C\u90A3\u4E48\u79F0\u8FD9\u4E9B\u65F6\u95F4\u4E3A<strong>\u51FA\u7AD9</strong>\uFF0C\u53CD\u4E4B\u4E3A\u5165\u7AD9</p><p><img src="https://s2.loli.net/2023/01/08/mMbcrDHi4NslkTf.png" alt=""></p><blockquote><p>\u4E2A\u4EBA\u7406\u89E3: \u51FA\u7AD9\u662F\u5411\u5916\u53D1\u9001\u6570\u636E\u5199\u5230\u4F20\u8F93\u5C42\u7684\u8FC7\u7A0B\uFF0C\u5165\u7AD9\u662F\u5E94\u7528\u4ECE\u4F20\u8F93\u5C42\u8BFB\u53D6\u6570\u636E\u7684\u8FC7\u7A0B</p></blockquote><h2 id="_6-channelhandleradapter" tabindex="-1"><a class="header-anchor" href="#_6-channelhandleradapter" aria-hidden="true">#</a> 6. <code>ChannelHandlerAdapter</code></h2><p>\u6709\u4E00\u4E9B\u9002\u914D\u5668\u7C7B\u53EF\u4EE5\u7F16\u5199\u81EA\u5B9A\u4E49\u7684ChannelHandler\u6240\u9700\u8981\u7684\u52AA\u529B\u964D\u4F4E\u5230\u6700\u4F4E\u9650\u5EA6\uFF0C\u56E0\u4E3A\u4ED6\u4EEC\u63D0\u4F9B\u4E86\u5B9A\u4E49\u5728\u5BF9\u5E94\u63A5\u53E3\u4E2D\u7684\u6240\u6709\u65B9\u6CD5\u7684\u9ED8\u8BA4\u5B9E\u73B0</p><p>\u5E38\u7528\u7684\u9002\u914D\u5668\u7C7B\uFF1A</p><ul><li>ChannelHandlerAdapter</li><li>ChannelInboundHandlerAdapter</li><li>ChannelOutboundHandlerAdapter</li><li>ChannelDuplexHandler</li></ul><h3 id="_6-1-\u7F16\u7801\u5668\u548C\u89E3\u7801\u5668" tabindex="-1"><a class="header-anchor" href="#_6-1-\u7F16\u7801\u5668\u548C\u89E3\u7801\u5668" aria-hidden="true">#</a> 6.1 \u7F16\u7801\u5668\u548C\u89E3\u7801\u5668</h3><p>\u5F53Netty\u53D1\u9001\u6216\u8005\u63A5\u6536\u4E00\u4E2A\u6D88\u606F\u65F6\u4F1A\u53D1\u751F\u4E00\u6B21\u6570\u636E\u8F6C\u6362\u3002\u5165\u7AD9\u6D88\u606F\u4F1A\u88AB\u89E3\u7801\uFF0C\u51FA\u7AD9\u6D88\u606F\u4F1A\u88AB\u7F16\u7801</p><p>\u6240\u6709\u7531Netty\u63D0\u4F9B\u7684\u7F16\u89E3\u7801\u5668\u9002\u914D\u5668\u7C7B\u90FD\u5B9E\u73B0\u4E86ChannelOutboundHandler\u6216\u8005ChannelInboundHandler\u63A5\u53E3</p><p>\u5BF9\u4E8E\u6BCF\u4E2A\u4ECE\u5165\u7AD9Channel\u8BFB\u53D6\u7684\u6D88\u606F\uFF0C\u90FD\u4F1A\u8C03\u7528channelRead\u65B9\u6CD5\uFF0C\u4E4B\u540E\u5C06\u8C03\u7528\u9884\u7F6E\u89E3\u7801\u5668\u63D0\u4F9B\u7684decode()\u65B9\u6CD5\uFF0C\u5E76\u5C06\u5DF2\u89E3\u7801\u7684\u5B57\u8282\u8F6C\u53D1\u7ED9ChannelPipeline\u4E2D\u7684\u4E0B\u4E00\u4E2AChannelInboundHandler\u3002\u51FA\u7AD9\u76F8\u53CD\u3002</p><h3 id="_6-2-simplechannelinboundhandler" tabindex="-1"><a class="header-anchor" href="#_6-2-simplechannelinboundhandler" aria-hidden="true">#</a> 6.2 SimpleChannelInboundHandler</h3><p>\u62BD\u8C61\u7C7B\uFF0C\u6700\u91CD\u8981\u7684\u65B9\u6CD5\u662FchannelRead0</p><h2 id="_7-bootstrapping" tabindex="-1"><a class="header-anchor" href="#_7-bootstrapping" aria-hidden="true">#</a> 7. Bootstrapping</h2><p>Netty\u7684\u5F15\u5BFC\u7C7B\u4E3A\u5E94\u7528\u7A0B\u5E8F\u7684\u7F51\u7EDC\u5C42\u914D\u7F6E\u63D0\u4F9B\u4E86\u5BB9\u5668\uFF0C\u6D89\u53CA\u5C06\u4E00\u4E2A\u8FDB\u7A0B\u7ED1\u5B9A\u5230\u67D0\u4E2A\u6307\u5B9A\u7684\u7AEF\u53E3\uFF0C\u6216\u8005\u5C06\u4E00\u4E2A\u8FDB\u7A0B\u8FDE\u63A5\u5230\u53E6\u4E00\u4E2A\u8FD0\u884C\u5728\u67D0\u4E2A\u6307\u5B9A\u4E3B\u673A\u7684\u7684\u6307\u5B9A\u7AEF\u53E3\u7684\u8FDB\u7A0B</p><ul><li><p>\u670D\u52A1\u5668ServerBootstrap: \u7ED1\u5B9A\u5230\u4E00\u4E2A\u672C\u5730\u7AEF\u53E3\uFF0C\u67092\u4E2AEventLoopGroup\uFF0C\u76D1\u542C\u8FDE\u63A5</p></li><li><p>\u5BA2\u6237\u7AEFBootstrap: \u8FDE\u63A5\u5230\u8FDC\u7A0B\u4E3B\u673A\u548C\u7AEF\u53E3\uFF0C\u67091\u4E2AEventLoopGroup</p></li></ul><p>\u670D\u52A1\u5668\u9700\u8981\u4E24\u7EC4\u4E0D\u540C\u7684Channel\u3002\u7B2C\u4E00\u7EC4\u53EA\u5305\u542B\u4E00\u4E2AServerChannel\uFF0C\u4EE3\u8868\u670D\u52A1\u5668\u81EA\u8EAB\u7684\u5DF2\u7ED1\u5B9A\u5230\u67D0\u4E2A\u7AEF\u53E3\u7684\u6B63\u5728\u76D1\u542C\u7684\u5957\u63A5\u5B57\uFF1B\u7B2C\u4E8C\u7EC4\u5C06\u5305\u542B\u6240\u6709\u5DF2\u521B\u5EFA\u7684\u7528\u6765\u5904\u7406\u4F20\u5165\u5BA2\u6237\u7AEF\u8FDE\u63A5(\u5BF9\u4E8E\u6BCF\u4E2A\u670D\u52A1\u5668\u5DF2\u7ECF\u63A5\u53D7\u7684\u8FDE\u63A5\u90FD\u6709\u4E00\u4E2A)\u7684Channel</p><p>\u4E0EServerChannel\u76F8\u5173\u8054\u7684EventLoopGroup\u5C06\u5206\u914D\u4E00\u4E2A\u8D1F\u8D23\u4E3A\u4F20\u5165\u8FDE\u63A5\u8BF7\u6C42\u521B\u5EFAChannel\u7684EventLoop\uFF0C\u4E00\u65E6\u8FDE\u63A5\u88AB\u63A5\u53D7\uFF0C\u7B2C\u4E8C\u4E2AEventLoop\u5C31\u4F1A\u7ED9\u5B83\u7684Channel\u5206\u914D\u4E00\u4E2AEventLoop</p><p><img src="https://s2.loli.net/2023/01/09/WB9QvXyJiVOu5aL.png" alt=""></p>',39),t=[d];function h(i,r){return n(),a("div",null,t)}const p=e(o,[["render",h],["__file","components_design.html.vue"]]);export{p as default};
