import{_ as n,r as c,o as d,c as i,a as e,b as o,d as r,e as t}from"./app.94cd83f3.js";const p={},s=e("h1",{id:"spring",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#spring","aria-hidden":"true"},"#"),o(" Spring")],-1),l=e("img",{src:"https://spring.io/images/spring-logo-2022-dark-2f10e8055653ec50e693eb444291d742.svg",alt:""},null,-1),h={href:"https://spring.io/",target:"_blank",rel:"noopener noreferrer"},g=e("h2",{id:"_1-spring\u5168\u5BB6\u6876\u4E2D\u6709\u54EA\u4E9B\u9879\u76EE",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#_1-spring\u5168\u5BB6\u6876\u4E2D\u6709\u54EA\u4E9B\u9879\u76EE","aria-hidden":"true"},"#"),o(" 1. Spring\u5168\u5BB6\u6876\u4E2D\u6709\u54EA\u4E9B\u9879\u76EE?")],-1),u={href:"https://spring.io/projects",target:"_blank",rel:"noopener noreferrer"},b=t(`<p><code>Spring Framework</code> (\u4E00\u822C<code>Spring Framework</code>\u7B80\u79F0\u4E3A<code>spring</code>)\u3001<code>Spring Boot</code>\u3001<code>Spring Cloud</code>\u3001<code>Spring Data</code>\u3001<code>Spring Security</code>\u3001<code>Spring Batch</code>\u3001<code>Spring Session</code>\u7B49</p><h2 id="_2-spring\u6709\u54EA\u4E9B\u6A21\u5757\u7EC4\u6210" tabindex="-1"><a class="header-anchor" href="#_2-spring\u6709\u54EA\u4E9B\u6A21\u5757\u7EC4\u6210" aria-hidden="true">#</a> 2. Spring\u6709\u54EA\u4E9B\u6A21\u5757\u7EC4\u6210</h2><p><code>core</code>\u3001<code>context</code>\u3001<code>beans</code>\u3001<code>AOP</code> \u3001<code>ORM</code>\u3001<code>WebMVC</code></p><h2 id="_3-spring\u4E2D\u7528\u5230\u4E86\u54EA\u4E9B\u8BBE\u8BA1\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#_3-spring\u4E2D\u7528\u5230\u4E86\u54EA\u4E9B\u8BBE\u8BA1\u6A21\u5F0F" aria-hidden="true">#</a> 3. Spring\u4E2D\u7528\u5230\u4E86\u54EA\u4E9B\u8BBE\u8BA1\u6A21\u5F0F</h2><p>\u5DE5\u5382\u6A21\u5F0F(<code>BeanFactory</code>)\uFF0C\u5355\u4F8B/\u539F\u578B\u6A21\u5F0F(bean\u7684\u4F5C\u7528\u57DF)\uFF0C\u4EE3\u7406\u6A21\u5F0F(AOP),\u9002\u914D\u5668\u6A21\u5F0F\uFF0C\u88C5\u9970\u5668\u6A21\u5F0F\uFF0C\u89C2\u5BDF\u8005\u6A21\u5F0F(\u4E8B\u4EF6\u76D1\u542C)\uFF0C\u7B56\u7565\u6A21\u5F0F(\u5B9E\u4F8B\u5316\u5BF9\u8C61)\uFF0C\u6A21\u677F\u6A21\u5F0F(JdbcTemplate)\uFF0C\u7EC4\u5408\u6A21\u5F0F</p><h2 id="_4-ioc\u5BB9\u5668\u662F\u4EC0\u4E48-\u5982\u4F55\u5B9E\u73B0\u7B80\u5355\u7684ioc\u5BB9\u5668-\u5728spring\u4E2D\u7684\u5B9E\u73B0\u65B9\u5F0F\u662F\u4EC0\u4E48-\u548Cdi\u6709\u4EC0\u4E48\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#_4-ioc\u5BB9\u5668\u662F\u4EC0\u4E48-\u5982\u4F55\u5B9E\u73B0\u7B80\u5355\u7684ioc\u5BB9\u5668-\u5728spring\u4E2D\u7684\u5B9E\u73B0\u65B9\u5F0F\u662F\u4EC0\u4E48-\u548Cdi\u6709\u4EC0\u4E48\u533A\u522B" aria-hidden="true">#</a> 4. IoC\u5BB9\u5668\u662F\u4EC0\u4E48\uFF0C\u5982\u4F55\u5B9E\u73B0\u7B80\u5355\u7684IoC\u5BB9\u5668\uFF0C\u5728Spring\u4E2D\u7684\u5B9E\u73B0\u65B9\u5F0F\u662F\u4EC0\u4E48\uFF0C\u548CDI\u6709\u4EC0\u4E48\u533A\u522B</h2><p>IoC,\u63A7\u5236\u53CD\u8F6C\uFF0C\u5C06\u5BF9\u8C61\u5B9E\u4F8B\u5316\u548C\u7BA1\u7406\u7684\u6743\u5229\u4EA4\u7ED9\u4E86\u5BB9\u5668\uFF0C\u53EF\u4EE5\u901A\u8FC7\u53CD\u5C04+\u5DE5\u5382\u6A21\u5F0F\u5B9E\u73B0\u7B80\u5355\u7684IoC\u5BB9\u5668;</p><p>\u5728Spring\u4E2D\u6709<code>DI</code>(\u4F9D\u8D56\u6CE8\u5165)\u548C<code>DL</code>\uFF08\u4F9D\u8D56\u67E5\u627E\uFF09\u4E24\u79CD\u5B9E\u73B0\u65B9\u5F0F\uFF0C<code>DI</code>\u53EF\u4EE5\u901A\u8FC7xml\u914D\u7F6E\u6587\u4EF6\u3001\u6CE8\u89E3\u9A71\u52A8(<code>@Value</code>/<code>@Autowired</code>/<code>@Inject</code>/<code>@Resource</code>)\u3001\u501F\u52A9<code>Aware</code>\u7CFB\u5217\u63A5\u53E3\u7B49\u65B9\u5F0F\u5B9E\u73B0\uFF0C\u800C<code>DL</code>\u6307\u7684\u662F\u5982<code>getBean(),ofType(),withAnnotation</code>\u7B49\u65B9\u5F0F\u4ECEIoC\u5BB9\u5668\u4E2D\u83B7\u53D6bean;</p><p>IoC\u548CDI\u533A\u522B\u5728\u4E8E\u89D2\u5EA6\u7684\u4E0D\u540C\uFF0C\u5728<strong>\u5BF9\u8C61\u7684\u89D2\u5EA6</strong>\uFF0CIoC\u5C06\u5BF9\u8C61\u5B9E\u4F8B\u5316\u548C\u7BA1\u7406\u7684\u6743\u5229\u4EA4\u7ED9\u4E86\u5BB9\u5668\uFF0C\u5728<strong>\u5BB9\u5668\u7684\u89D2\u5EA6</strong>,\u5BB9\u5668\u5C06\u5BF9\u8C61\u4F9D\u8D56\u7684\u5176\u4ED6\u5BF9\u8C61\u6CE8\u5165</p><h2 id="_5-beanfactory\u3001applicationcontext\u3001factorybean\u7684\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#_5-beanfactory\u3001applicationcontext\u3001factorybean\u7684\u533A\u522B" aria-hidden="true">#</a> 5. <code>BeanFactory</code>\u3001<code>ApplicationContext</code>\u3001<code>FactoryBean</code>\u7684\u533A\u522B</h2><p><code>BeanFactory</code>\u662F\u4E00\u4E2ASpring\u6846\u67B6\u81EA\u5DF1\u4F7F\u7528\u7684\u63D0\u4F9B\u4E86\u62BD\u8C61\u7684\u914D\u7F6E\u548C\u5BF9\u8C61\u7684\u7BA1\u7406\u673A\u5236\u7684\u63A5\u53E3;</p><p><code>ApplicationContext</code>\u662F<code>BeanFactory</code>\u7684\u5B50\u63A5\u53E3(\u51C6\u786E\u7684\u8BF4\u662F<code>BeanFactory</code>\u5B50\u63A5\u53E3<code>ListableBeanFactory</code>\u548C<code>HierarchicalBeanFactory</code>\u7684\u5B50\u63A5\u53E3)\uFF0C\u6269\u5C55\u4E86\u8BB8\u591A\u529F\u80FD\u5982: \u751F\u547D\u5468\u671F\u7684\u7BA1\u7406\uFF0C\u4E8B\u4EF6\u673A\u5236\u548C\u6D88\u606F\u4E0E\u56FD\u9645\u5316\u7B49\u7B49\uFF0C\u5177\u4F53\u7684\uFF0C\u7C7B<code>GenericApplicationContext</code>\u5B9E\u73B0\u4E86<code>ApplicationContext</code>,\u4E5F\u91C7\u7528\u7EC4\u5408\u6A21\u5F0F\u4F7F\u7528\u4E86<code>DefaultListableBeanFactory</code>;</p><p><code>FactoryBean</code>\u5219\u662F\u521B\u5EFA\u5BF9\u8C61\u7684\u5DE5\u5382Bean\uFF0C\u53EF\u4EE5\u4F7F\u7528\u5B83\u6765\u521B\u5EFA\u4E00\u4E9B\u521D\u59CB\u5316\u6D41\u7A0B\u590D\u6742\u7684\u5BF9\u8C61</p><h2 id="_6-bean\u7684\u751F\u547D\u5468\u671F" tabindex="-1"><a class="header-anchor" href="#_6-bean\u7684\u751F\u547D\u5468\u671F" aria-hidden="true">#</a> 6. Bean\u7684\u751F\u547D\u5468\u671F</h2><ol><li>\u4ECEXML\u914D\u7F6E\u6216\u8005\u6CE8\u89E3\u914D\u7F6E\u4E2D\u83B7\u53D6<code>BeanDefinition</code>,\u63CF\u8FF0\u4E86bean\u7684\u5143\u4FE1\u606F\uFF0C\u5305\u62EC\u7D2F\u4FE1\u606F\u3001\u5C5E\u6027\u3001\u884C\u4E3A\u548C\u4F9D\u8D56\u5173\u7CFB\u7B49</li><li>\u5B9E\u4F8B\u5316Bean\uFF0C\u5E76\u8BBE\u7F6E\u5C5E\u6027</li><li>\u8C03\u7528<code>Aware</code>\u65B9\u6CD5(\u5982\u679C\u5B9E\u73B0\u4E86<code>BeanNameAware</code>,<code>BeanFactoryAware</code>,<code>ApplicationContextAware</code>\u7B49\u63A5\u53E3)</li><li>\u540E\u7F6E\u5904\u7406\u5668\u7684\u9884\u5904\u7406\u65B9\u6CD5 \uFF08<code>BeanPostProcessor.postPreocessBeforeInitialization</code>\uFF09</li><li>\u521D\u59CB\u5316Bean (\u5305\u542B\u4E24\u6B65: \u5B9E\u73B0<code>InitializingBean.afterPropertiesSet</code>\u548C\u81EA\u5B9A\u4E49\u7684\u521D\u59CB\u5316\u65B9\u6CD5)</li><li>\u540E\u7F6E\u5904\u7406\u5668\u7684\u540E\u5904\u7406\u65B9\u6CD5 \uFF08<code>BeanPostProcessor.postPreocessAfterInitialization</code>\uFF09</li><li>bean\u4F7F\u7528\u540E\u7684\u9500\u6BC1\u65B9\u6CD5 (\u5305\u542B\u4E24\u6B65: <code>DisposableBean.destroy</code>\u548C\u81EA\u5B9A\u4E49\u7684\u9500\u6BC1\u65B9\u6CD5)</li></ol><h2 id="_7-spring-\u7684\u4F5C\u7528\u57DF-\u4F9D\u8D56\u6CE8\u5165\u65B9\u5F0F-\u81EA\u52A8\u88C5\u914D\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#_7-spring-\u7684\u4F5C\u7528\u57DF-\u4F9D\u8D56\u6CE8\u5165\u65B9\u5F0F-\u81EA\u52A8\u88C5\u914D\u65B9\u5F0F" aria-hidden="true">#</a> 7. Spring \u7684\u4F5C\u7528\u57DF\uFF0C\u4F9D\u8D56\u6CE8\u5165\u65B9\u5F0F\uFF0C\u81EA\u52A8\u88C5\u914D\u65B9\u5F0F</h2><h3 id="_7-1-\u4F5C\u7528\u57DFscope" tabindex="-1"><a class="header-anchor" href="#_7-1-\u4F5C\u7528\u57DFscope" aria-hidden="true">#</a> 7.1 \u4F5C\u7528\u57DFScope</h3><p>\u5355\u4F8B\uFF0C\u539F\u578B\uFF0C<code>Request</code>,<code>Session</code>,<code>Global Session</code></p><h3 id="_7-2-\u4F9D\u8D56\u6CE8\u5165\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#_7-2-\u4F9D\u8D56\u6CE8\u5165\u65B9\u5F0F" aria-hidden="true">#</a> 7.2 \u4F9D\u8D56\u6CE8\u5165\u65B9\u5F0F</h3><p>\u6784\u9020\u6CE8\u5165\uFF0Csetter\u6CE8\u5165\uFF0C\u9759\u6001\u5DE5\u5382\u6CE8\u5165(<code>factory-method</code>)\uFF0C\u5B9E\u4F8B\u5DE5\u5382\u6CE8\u5165(<code>factory-bean</code>)</p><h3 id="_7-3-\u81EA\u52A8\u88C5\u914D\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#_7-3-\u81EA\u52A8\u88C5\u914D\u65B9\u5F0F" aria-hidden="true">#</a> 7.3 \u81EA\u52A8\u88C5\u914D\u65B9\u5F0F</h3><p>no,<code>byName</code>,<code>byType</code>,<code>constructor</code>,<code>auto</code>\uFF08auto\u4E0B\u5148constructor\u540EbyType\uFF09</p><h2 id="_8-spring\u5982\u4F55\u89E3\u51B3\u5FAA\u73AF\u4F9D\u8D56" tabindex="-1"><a class="header-anchor" href="#_8-spring\u5982\u4F55\u89E3\u51B3\u5FAA\u73AF\u4F9D\u8D56" aria-hidden="true">#</a> 8. Spring\u5982\u4F55\u89E3\u51B3\u5FAA\u73AF\u4F9D\u8D56</h2><p>\u5FAA\u73AF\u4F9D\u8D56\u6307\u7684\u662F\u4E24\u4E2Abean\u4E2D\u5B58\u5728\u76F8\u4E92\u4F9D\u8D56\u5F15\u7528\uFF0C\u5982A.b=B,B.a=A\uFF0C\u53EF\u4EE5\u5206\u4E3A\u6784\u9020\u5668\u7684\u5FAA\u73AF\u4F9D\u8D56\u548CField\u5C5E\u6027\u7684\u5FAA\u73AF\u4F9D\u8D56(setter\u6CE8\u5165)</p><p>\u6784\u9020\u5668\u6CE8\u5165\u7684\u5FAA\u73AF\u4F9D\u8D56\u65E0\u6CD5\u89E3\u51B3\uFF0C\u53EA\u80FD\u629B\u51FA\u5F02\u5E38\uFF0C</p><p>\u5BF9\u4E8E\u5C5E\u6027\u6CE8\u5165\u7684\u5FAA\u73AF\u4F9D\u8D56\uFF0CSpring\u91C7\u7528\u4E09\u7EA7\u7F13\u5B58\u63D0\u524D\u66B4\u9732\u5BF9\u8C61\u7684\u65B9\u6CD5\u89E3\u51B3\uFF0C\u5728IoC\u5BB9\u5668\u4E2D\u6709\u6240\u8C13\u7684&quot;\u4E09\u7EA7\u7F13\u5B58&quot;\uFF0C\u5206\u522B\u662F</p><p>\u4E00\u7EA7: <code>ConcurrentHashMap singletonObjects</code>,\u4E8C\u7EA7: <code>HashMap earlySingletonObjects</code>,\u4E09\u7EA7: <code>HashMap singletonFactries</code>\uFF0C</p><ol><li>A\u5728\u5B9E\u4F8B\u5316\u540E\u5C06\u81EA\u5DF1\u653E\u5165\u4E09\u7EA7\u7F13\u5B58</li><li>B\u521B\u5EFA\u8FC7\u7A0B\u4E2D\u53D1\u73B0\u4F9D\u8D56A,\u6309\u7167\u4E00\u7EA7&gt;\u4E8C\u7EA7&gt;\u4E09\u7EA7\u7F13\u5B58\u7684\u987A\u5E8F\u5728\u4E09\u7EA7\u7F13\u5B58\u4E2D\u627E\u5230A\uFF0C\u5E76\u5C06A\u4ECE\u4E09\u7EA7\u79FB\u52A8\u5230\u4E8C\u7EA7\u7F13\u5B58</li><li>B\u521B\u5EFA\u5B8C\u6210\uFF0C\u5C06B\u79FB\u52A8\u5230\u4E00\u7EA7\u7F13\u5B58</li><li>A\u6309\u7167\u4E00\u7EA7&gt;\u4E8C\u7EA7&gt;\u4E09\u7EA7\u7F13\u5B58\u7684\u987A\u5E8F\u5728\u4E00\u7EA7\u7F13\u5B58\u4E2D\u627E\u5230B\uFF0C\u5B8C\u6210\u4E86A\u548CB\u4E24\u4E2Abean\u7684\u521B\u5EFA</li></ol><p>\u5728\u5B9E\u9645\u4E2D\uFF0C\u4E5F\u53EF\u4EE5\u901A\u8FC7<code>@Lazy</code>\u5EF6\u8FDF\u6CE8\u5165\u7684\u65B9\u5F0F\u89E3\u51B3\u5FAA\u73AF\u4F9D\u8D56\uFF0C\u4F46\u662F\u6700\u597D\u4E0D\u8981\u6709\u5FAA\u73AF\u4F9D\u8D56</p><ul><li><p>\u4E3A\u4EC0\u4E48\u6784\u9020\u6CE8\u5165\u4E0D\u884C\uFF1F</p><p>\u56E0\u4E3A\u9700\u8981\u5148\u5B9E\u4F8B\u5316\u653E\u5165\u4E09\u7EA7\u7F13\u5B58\uFF0C\u5982\u679C\u5FAA\u73AF\u4F9D\u8D56\u5728\u6784\u9020\u6CE8\u5165\u4E2D\u5219\u5B8C\u4E0D\u6210\u5B9E\u4F8B\u5316</p></li><li><p>\u4E3A\u4EC0\u4E48\u9700\u8981\u4E09\u7EA7\u7F13\u5B58\uFF0C\u4E8C\u7EA7\u662F\u5426\u80FD\u6EE1\u8DB3\u8981\u6C42?</p><p>\u4E8C\u7EA7\u7F13\u5B58\u53EF\u4EE5\u6EE1\u8DB3\u89E3\u51B3\u5FAA\u73AF\u4F9D\u8D56\u7684\u8981\u6C42\uFF0C\u589E\u52A0\u5230\u4E09\u7EA7\u7F13\u5B58\u65F6\u4E3A\u4E86\u4E09\u7EA7\u5347\u4E8C\u7EA7\u7F13\u5B58\u65F6\u53EF\u4EE5\u6269\u5C55\uFF0C\u4E3B\u8981\u5E94\u7528\u5728AOP\u4EE3\u7406\u4E0A</p></li></ul><h2 id="_9-\u4EC0\u4E48\u662Faop-spring\u4E2Daop\u6709\u54EA\u4E9B\u901A\u77E5\u7C7B\u578B-\u5927\u6982\u600E\u4E48\u4F7F\u7528-\u6709\u54EA\u4E9B\u4F7F\u7528\u573A\u666F-\u6709\u54EA\u4E9B\u5931\u6548\u573A\u666F-\u5E95\u5C42\u539F\u7406\u662F\u600E\u6837\u7684" tabindex="-1"><a class="header-anchor" href="#_9-\u4EC0\u4E48\u662Faop-spring\u4E2Daop\u6709\u54EA\u4E9B\u901A\u77E5\u7C7B\u578B-\u5927\u6982\u600E\u4E48\u4F7F\u7528-\u6709\u54EA\u4E9B\u4F7F\u7528\u573A\u666F-\u6709\u54EA\u4E9B\u5931\u6548\u573A\u666F-\u5E95\u5C42\u539F\u7406\u662F\u600E\u6837\u7684" aria-hidden="true">#</a> 9. \u4EC0\u4E48\u662FAOP,Spring\u4E2DAOP\u6709\u54EA\u4E9B\u901A\u77E5\u7C7B\u578B,\u5927\u6982\u600E\u4E48\u4F7F\u7528,\u6709\u54EA\u4E9B\u4F7F\u7528\u573A\u666F,\u6709\u54EA\u4E9B\u5931\u6548\u573A\u666F,\u5E95\u5C42\u539F\u7406\u662F\u600E\u6837\u7684\uFF1F</h2><h3 id="_9-1-aop\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#_9-1-aop\u6982\u8FF0" aria-hidden="true">#</a> 9.1 AOP\u6982\u8FF0</h3><p>AOP\u662F\u5728\u4E0D\u4FEE\u6539\u4EE3\u7801\u7684\u524D\u63D0\u4E0B\uFF0C\u4F7F\u7528\u8FD0\u884C\u65F6\u52A8\u6001\u4EE3\u7406\u5BF9\u4EE3\u7801\u589E\u5F3A</p><h3 id="_9-2-spring\u4E2Daop\u6709\u54EA\u4E9B\u901A\u77E5\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#_9-2-spring\u4E2Daop\u6709\u54EA\u4E9B\u901A\u77E5\u7C7B\u578B" aria-hidden="true">#</a> 9.2 Spring\u4E2DAOP\u6709\u54EA\u4E9B\u901A\u77E5\u7C7B\u578B</h3><p>\u524D\u7F6E\u901A\u77E5\uFF0C\u540E\u7F6E\u901A\u77E5\uFF0C\u8FD4\u56DE\u540E\u901A\u77E5\uFF0C\u5F02\u5E38\u901A\u77E5\uFF0C\u73AF\u7ED5\u901A\u77E5</p><h3 id="_9-3-\u5927\u6982\u600E\u4E48\u4F7F\u7528" tabindex="-1"><a class="header-anchor" href="#_9-3-\u5927\u6982\u600E\u4E48\u4F7F\u7528" aria-hidden="true">#</a> 9.3 \u5927\u6982\u600E\u4E48\u4F7F\u7528</h3><p>\u5F15\u5165\u4F9D\u8D56\uFF0C\u5B9A\u4E49\u5207\u9762\u7C7B(\u901A\u8FC7<code>@Aspect</code>\u6216\u8005.aj\u6587\u4EF6)\uFF0C\u6307\u5B9A\u5207\u70B9\uFF0C\u7F16\u5199\u901A\u77E5\u65B9\u6CD5\uFF0C\u901A\u8FC7\u5207\u70B9\u83B7\u53D6\u76F8\u5E94\u4FE1\u606F\u5904\u7406\u903B\u8F91</p><h3 id="_9-4-\u6709\u54EA\u4E9B\u4F7F\u7528\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#_9-4-\u6709\u54EA\u4E9B\u4F7F\u7528\u573A\u666F" aria-hidden="true">#</a> 9.4 \u6709\u54EA\u4E9B\u4F7F\u7528\u573A\u666F</h3><p>\u4E8B\u52A1\u63A7\u5236<code>@Transcational</code>\uFF0C\u6743\u9650\u6821\u9A8C\uFF0C\u65E5\u5FD7\u5207\u9762\uFF0C\u6570\u636E\u7F13\u5B58\u7B49</p><h3 id="_9-5-\u6709\u54EA\u4E9B\u5931\u6548\u573A\u666F-transactional\u6709\u54EA\u4E9B\u5931\u6548\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#_9-5-\u6709\u54EA\u4E9B\u5931\u6548\u573A\u666F-transactional\u6709\u54EA\u4E9B\u5931\u6548\u573A\u666F" aria-hidden="true">#</a> 9.5 \u6709\u54EA\u4E9B\u5931\u6548\u573A\u666F/<code>@Transactional</code>\u6709\u54EA\u4E9B\u5931\u6548\u573A\u666F</h3><ul><li>\u4F5C\u4E3AAOP\uFF0C\u662F\u8981\u751F\u6210\u52A8\u6001\u4EE3\u7406\uFF0C\u5982\u679C\u91C7\u7528<code>private</code>\u4FEE\u9970\uFF0C\u6216\u8005\u4F5C\u7528\u5728\u63A5\u53E3\u4E0A\u4F46\u662F\u4F7F\u7528<code>CGLib</code>\u52A8\u6001\u4EE3\u7406\uFF0C\u6216\u8005\u8C03\u7528\u81EA\u8EAB\u53E6\u4E00\u4E2A\u6709\u4E8B\u52A1\u63A7\u5236\u7684\u65B9\u6CD5\u90FD\u4F1A\u5931\u6548</li><li>\u4F5C\u4E3A\u6CE8\u89E3\u5185\u5BB9\u672C\u8EAB\uFF0C\u8981\u5BF9\u5F02\u5E38\u6355\u83B7\u540E\u624D\u80FD\u5904\u7406\u4E8B\u52A1\u56DE\u6EDA\uFF0C\u9ED8\u8BA4\u6355\u83B7<code>RuntimeException</code>,\u5982\u679C\u6CA1\u6709\u6307\u5B9A\u5F02\u5E38\u7C7B\u578B\u6216\u8005\u6307\u5B9A\u7684\u5F02\u5E38\u4E0D\u5305\u542B(\u5982\u629B\u51FAException)\uFF0C\u6216\u8005\u6355\u83B7\u5F02\u5E38\u540E\u6CA1\u6709\u518D\u629B\u51FA \uFF0C\u90FD\u4F1A\u5BFC\u81F4<code>Transactional</code>\u5931\u6548</li></ul><h3 id="_9-6-\u5E95\u5C42\u539F\u7406\u662F\u600E\u6837-\u7B80\u8FF0" tabindex="-1"><a class="header-anchor" href="#_9-6-\u5E95\u5C42\u539F\u7406\u662F\u600E\u6837-\u7B80\u8FF0" aria-hidden="true">#</a> 9.6 \u5E95\u5C42\u539F\u7406\u662F\u600E\u6837(\u7B80\u8FF0)\uFF1F</h3><p>\u501F\u52A9\u540E\u7F6E\u5904\u7406\u5668\uFF0C\u5728bean\u521D\u59CB\u5316\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u5C06\u76EE\u6807\u5BF9\u8C61\u5305\u88C5\u4E3A\u4EE3\u7406\u5BF9\u8C61\u3002\u4EE3\u7406\u5BF9\u8C61\u6784\u9020\u540E\uFF0C\u6267\u884C\u65B9\u6CD5\u8FDB\u5165\u5230\u4EE3\u7406\u7C7B\u4E2D\u4F9D\u6B21\u6267\u884C\u7EC7\u5165\u901A\u77E5</p><p>Spring\u4E2D\u53EF\u4EE5\u9009\u62E9JDK\u4E2D\u7684<code>Proxy</code>\u6216\u8005<code>CGLib</code>\u8FDB\u884C\u52A8\u6001\u4EE3\u7406\uFF0C\u4E24\u8005\u7684\u533A\u522B\uFF1AJDK\u4EE3\u7406\u81F3\u5C11\u9700\u8981\u5B9E\u73B0\u4E00\u4E2A\u63A5\u53E3\uFF0C\u521B\u5EFA\u5FEB\u6267\u884C\u6162\uFF0C<code>CGLib</code>\u5C5E\u4E8E\u5B57\u8282\u7801\u589E\u5F3A\uFF0C\u521B\u5EFA\u6162\u6267\u884C\u5FEB</p><blockquote><p>\u6709\u54EA\u4E9B\u5B57\u8282\u7801\u589E\u5F3A\u7684\u65B9\u5F0F: <code>ASM</code>,<code>Javaassist</code>,<code>Byte-Buddy</code></p></blockquote><h2 id="_10-spring\u4E8B\u52A1\u7684\u4F20\u64AD\u884C\u4E3A\u6709\u54EA\u4E9B" tabindex="-1"><a class="header-anchor" href="#_10-spring\u4E8B\u52A1\u7684\u4F20\u64AD\u884C\u4E3A\u6709\u54EA\u4E9B" aria-hidden="true">#</a> 10. Spring\u4E8B\u52A1\u7684\u4F20\u64AD\u884C\u4E3A\u6709\u54EA\u4E9B</h2><blockquote><p><code>org.springframework.transaction.TransactionDefinition</code></p></blockquote><ul><li><code>PROPAGATION_REQUIRED</code> \u5F53\u524D\u6CA1\u4E8B\u52A1\u5219\u5F00\u542F\u65B0\u4E8B\u52A1\uFF1B\u5F53\u524D\u6709\u4E8B\u52A1\u5219\u8FD0\u884C\u5728\u5F53\u524D\u4E8B\u52A1\u4E2D</li><li><code>PROPAGATION_SUPPORTS</code> \u5F53\u524D\u6CA1\u4E8B\u52A1\u5219\u4E0D\u521B\u5EFA\u4E8B\u52A1\uFF1B\u5F53\u524D\u6709\u4E8B\u52A1\u5219\u8FD0\u884C\u5728\u5F53\u524D\u4E8B\u52A1\u4E2D</li><li><code>PROPAGATION_MANDATORY</code> \u5F53\u524D\u5FC5\u987B\u8FD0\u884C\u5728\u4E8B\u52A1\u4E2D\uFF0C\u6CA1\u6709\u5219\u629B\u51FA\u5F02\u5E38</li><li><code>PROPAGATION_REQUIRES_NEW</code> \u5F53\u524D\u6CA1\u4E8B\u52A1\u5219\u5F00\u542F\u65B0\u4E8B\u7269\uFF1B\u5F53\u524D\u6709\u4E8B\u52A1\u5219\u5C06\u539F\u4E8B\u52A1\u6682\u505C\uFF0C\u91CD\u65B0\u5F00\u542F\u65B0\u4E8B\u52A1\uFF0C\u65B0\u4E8B\u52A1\u5B8C\u6BD5\u540E\u518D\u5C06\u539F\u4E8B\u52A1\u91CA\u653E</li><li><code>PROPAGATION_NOT_SUPPORTED</code> \u5F53\u524D\u6CA1\u4E8B\u52A1\u5219\u4E0D\u5728\u4E8B\u52A1\u4E2D\u8FD0\u884C\uFF1B\u5F53\u524D\u6709\u4E8B\u52A1\u5219\u5C06\u4E8B\u52A1\u6682\u505C</li><li><code>PROPAGATION_NEVER</code> \u4E0D\u5141\u8BB8\u8FD0\u884C\u5728\u4E8B\u52A1\u4E2D\uFF0C\u6709\u4E8B\u52A1\u5219\u629B\u51FA\u5F02\u5E38</li><li><code>PROPAGATION_NESTED</code> \u5F53\u524D\u6CA1\u4E8B\u52A1\u5219\u5F00\u542F\u65B0\u4E8B\u52A1\uFF1B\u5F53\u524D\u6709\u4E8B\u52A1\u5219\u8BB0\u5F55\u4E00\u4E2A\u4FDD\u5B58\u70B9\uFF0C\u5E76\u7EE7\u7EED\u8FD0\u884C\u5728\u5F53\u524D\u4E8B\u52A1\uFF0C\u5982\u679C\u5B50\u4E8B\u52A1\u51FA\u73B0\u5F02\u5E38\u5219\u662F\u56DE\u6EDA\u5230\u4E0A\u4E00\u4E2A\u4FDD\u5B58\u70B9</li></ul><h2 id="_11-\u7B80\u8FF0spring-mvc\u7684\u5DE5\u4F5C\u6D41\u7A0B-\u62E6\u622A\u5668\u3001\u8FC7\u6EE4\u5668\u6709\u4EC0\u4E48\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#_11-\u7B80\u8FF0spring-mvc\u7684\u5DE5\u4F5C\u6D41\u7A0B-\u62E6\u622A\u5668\u3001\u8FC7\u6EE4\u5668\u6709\u4EC0\u4E48\u533A\u522B" aria-hidden="true">#</a> 11. \u7B80\u8FF0Spring MVC\u7684\u5DE5\u4F5C\u6D41\u7A0B\uFF0C\u62E6\u622A\u5668\u3001\u8FC7\u6EE4\u5668\u6709\u4EC0\u4E48\u533A\u522B</h2><h3 id="_11-1-spring-mvc\u7684\u5DE5\u4F5C\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#_11-1-spring-mvc\u7684\u5DE5\u4F5C\u6D41\u7A0B" aria-hidden="true">#</a> 11.1 Spring MVC\u7684\u5DE5\u4F5C\u6D41\u7A0B</h3><p>\u8BF7\u6C42\u53D1\u9001\u5230<code>DispatcherServlet</code>,\u901A\u8FC7<code>HandlerMapping</code>\u8FD4\u56DE\u5904\u7406\u5668\u6267\u884C\u94FE<code>HandlerExecutionChain</code>,\u901A\u8FC7<code>HandlerAdapter</code>\u6267\u884C<code>Handler</code>\u8FD4\u56DE<code>ModelAndView</code>,<code>DispatcherServlet</code>\u518D\u901A\u8FC7\u89C6\u56FE\u89E3\u6790\u5668\u5F97\u5230\u89C6\u56FE\uFF0C\u54CD\u5E94\u7ED9\u8C03\u7528\u65B9</p><h4 id="_11-2-\u62E6\u622A\u5668\u548C\u8FC7\u6EE4\u5668\u7684\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#_11-2-\u62E6\u622A\u5668\u548C\u8FC7\u6EE4\u5668\u7684\u533A\u522B" aria-hidden="true">#</a> 11.2 \u62E6\u622A\u5668\u548C\u8FC7\u6EE4\u5668\u7684\u533A\u522B</h4><p>\u4E24\u8005\u90FD\u662FAOP\u4ECE\u7F16\u7A0B\u601D\u60F3\u7684\u4F53\u73B0\uFF0C\u90FD\u80FD\u5B9E\u73B0\u6743\u9650\u68C0\u67E5\u3001\u65E5\u5FD7\u8BB0\u5F55\u7B49\uFF0C\u4E0D\u540C\u7684\u662F:</p><ul><li>\u4F7F\u7528\u8303\u56F4\u4E0D\u540C\uFF0C\u8FC7\u6EE4\u5668\u662F<code>Servlet</code>\u89C4\u8303\u89C4\u5B9A\u7684\uFF0C\u53EA\u7528\u4E8Eweb\u7A0B\u5E8F\uFF1B\u800C\u62E6\u622A\u5668\u4E5F\u53EF\u4EE5\u7528\u5728\u5E94\u7528\u7A0B\u5E8F\u4E2D</li><li>\u89C4\u8303\u4E0D\u540C\uFF0C\u8FC7\u6EE4\u5668\u662F<code>Servlet</code>\u5BB9\u5668\u652F\u6301\u7684\uFF0C\u800C\u62E6\u622A\u5668\u662F\u5728Spring\u5BB9\u5668\u5185\u7684</li><li>\u4F7F\u7528\u7684\u8D44\u6E90\u4E0D\u540C,\u62E6\u622A\u5668\u662FSpring\u7684\u7EC4\u4EF6\u53EF\u4EE5\u4F7F\u7528Spring\u7684\u8D44\u6E90\uFF0C\u800C\u8FC7\u6EE4\u5668\u4E0D\u80FD</li><li>\u4F5C\u7528\u8303\u56F4\u4E0D\u540C: \u8FC7\u6EE4\u5668\u53EA\u5728<code>Servlet</code>\u524D\u540E\u8D77\u4F5C\u7528\uFF0C\u800C\u62E6\u622A\u5668\u53EF\u4EE5\u5728\u65B9\u6CD5\u524D\u540E\u3001\u5F02\u5E38\u629B\u51FA\u524D\u540E\u3002\u4F18\u5148\u4F7F\u7528\u62E6\u622A\u5668</li></ul><h2 id="_12-spring-boot\u6BD4springmvc\u597D\u5728\u54EA-\u4E3A\u4EC0\u4E48\u4F7F\u7528springboot" tabindex="-1"><a class="header-anchor" href="#_12-spring-boot\u6BD4springmvc\u597D\u5728\u54EA-\u4E3A\u4EC0\u4E48\u4F7F\u7528springboot" aria-hidden="true">#</a> 12. Spring Boot\u6BD4SpringMVC\u597D\u5728\u54EA(\u4E3A\u4EC0\u4E48\u4F7F\u7528SpringBoot)\uFF1F</h2><p>Starter\u4F9D\u8D56\u6574\u5408\uFF0C\u81EA\u52A8\u914D\u7F6E\uFF0C\u5185\u5D4Cweb\u5BB9\u5668\u53EF\u72EC\u7ACB\u8FD0\u884C\uFF0C\u53EF\u76D1\u63A7\uFF0C\u8D8B\u5411\u5FAE\u670D\u52A1\uFF0C\u53EF\u5BB9\u5668\u5316</p><h2 id="_13-spring-boot\u5982\u4F55\u5B9E\u73B0\u81EA\u52A8\u914D\u7F6E" tabindex="-1"><a class="header-anchor" href="#_13-spring-boot\u5982\u4F55\u5B9E\u73B0\u81EA\u52A8\u914D\u7F6E" aria-hidden="true">#</a> 13. Spring Boot\u5982\u4F55\u5B9E\u73B0\u81EA\u52A8\u914D\u7F6E</h2><p>\u4E2A\u4EBA\u8BA4\u4E3A\u91CD\u70B9\u5728<code>@Import</code>\u548C<code>SPI</code>\u673A\u5236</p><p>\u901A\u8FC7\u542F\u52A8\u7C7B\u4E2D\u7684main\u65B9\u6CD5\u7684<code>SpringApplication.run(Xx.class, args);</code>\u8C03\u7528\u540E\uFF0C\u4F1A\u83B7\u53D6\u5230\u542F\u52A8\u7C7B\u4E0A\u7684\u7EC4\u5408\u6CE8\u89E3<code>@SpringBootApplication</code>,</p><p>\u5176\u4E2D<code>@EnableAutoConfiguration</code>\u5411\u4E0B\u53EF\u8FFD\u6EAF\u5230<code>@Import({AutoConfigurationImportSelector.class})</code>,</p><p>\u5728\u5BF9\u5E94\u7684\u7C7B\u4E2D\u901A\u8FC7<code>SPI</code>(<code>SpringFactoriesLoader.loadXxx</code>)\u673A\u5236\uFF0C\u52A0\u8F7D<code>META-INF/spring.factories</code>\u4E2D\u7684\u7C7B\uFF0C</p><p>\u901A\u8FC7<code>@Conditional</code>\u6765\u8FC7\u6EE4\u9700\u8981\u7684\u914D\u7F6E\u7C7B\uFF0C</p><p>\u7136\u540E\u8C03\u7528Spring\u7684<code>ConfigurableApplicationContext#refresh</code>\u65B9\u6CD5\u5C06bean\u88C5\u914D\u7684\u5BB9\u5668</p><h2 id="_14-spring-boot\u5982\u4F55\u81EA\u5B9A\u4E49starter" tabindex="-1"><a class="header-anchor" href="#_14-spring-boot\u5982\u4F55\u81EA\u5B9A\u4E49starter" aria-hidden="true">#</a> 14. Spring Boot\u5982\u4F55\u81EA\u5B9A\u4E49starter</h2><p>\u5F15\u5165<code>spring-boot-autoconfigure</code>\u4F9D\u8D56\uFF0C\u7F16\u5199\u5BF9\u5E94\u7684Bean(<code>EnableConfigurationProperties</code>,<code>ConfigurationProperties</code>),\u7F16\u5199\u914D\u7F6E\u7C7B<code>Configuration</code>,\u7F16\u5199</p><p><code> resources/META-INF/spring.factories</code>\u5982</p><div class="language-properties ext-properties line-numbers-mode"><pre class="language-properties"><code><span class="token key attr-name">org.springframework.boot.autoconfigure.EnableAutoConfiguration</span><span class="token punctuation">=</span><span class="token value attr-value">\\
  com.spectred.config.MyAutoConfiguration</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><p>\u4F7F\u7528\u65F6\u5F15\u5165\u5BF9\u5E94\u7684starter,\u6CE8\u5165\u5BF9\u5E94\u7684bean</p><h2 id="_15-spring-cloud\u6709\u54EA\u4E9B\u7EC4\u4EF6" tabindex="-1"><a class="header-anchor" href="#_15-spring-cloud\u6709\u54EA\u4E9B\u7EC4\u4EF6" aria-hidden="true">#</a> 15. Spring Cloud\u6709\u54EA\u4E9B\u7EC4\u4EF6</h2><blockquote><p>\u76EE\u524D\u6709Spring Cloud Netlix\uFF0CSpring Cloud Alibaba ,Spring Cloud Tencent\uFF0C\u5F53\u524D\u4E3B\u6D41\u7684\u662FSpring Cloud Alibaba</p></blockquote><p><strong>\u670D\u52A1\u6CE8\u518C\u4E2D\u5FC3</strong>: Eureka , <strong>Nacos</strong></p><p><strong>\u5BA2\u6237\u7AEF\u8D1F\u8F7D\u5747\u8861</strong>: Ribbon , DubboLB, <strong>Spring Cloud Loadbalancer</strong></p><p>API<strong>\u7F51\u5173</strong>: Zuul , <strong>Spring Cloud Gateway</strong></p><p><strong>\u7194\u65AD\u5668</strong>: Hystrix , <strong>Sentinel</strong></p><p><strong>\u914D\u7F6E\u4E2D\u5FC3</strong>: Spring Cloud Config , <strong>Nacos</strong></p><p><strong>\u670D\u52A1\u8C03\u7528</strong>: Feign , <strong>Dubbo</strong></p><p><strong>\u6D88\u606F\u9A71\u52A8</strong>: <strong>Spring Cloud Stream</strong></p><p><strong>\u94FE\u8DEF\u8FFD\u8E2A</strong>: <strong>Spring Cloud Sleuth/Zipkin</strong>\uFF0C<strong>SkyWalking</strong></p><p><strong>\u5206\u5E03\u5F0F\u4E8B\u52A1</strong>: <strong>Seata</strong></p>`,79);function _(S,f){const a=c("ExternalLinkIcon");return d(),i("div",null,[s,e("blockquote",null,[e("p",null,[l,e("a",h,[o("spring.io"),r(a)])])]),g,e("blockquote",null,[e("p",null,[e("a",u,[o("spring projects"),r(a)])])]),b])}const x=n(p,[["render",_],["__file","spring.html.vue"]]);export{x as default};
